INSERT INTO `movie` VALUES (49, '亲密敌人', 'Dear Enemy', '剧情,爱情', '黄立行,徐静蕾,李治廷', '徐静蕾', '2011-12-23中国大陆上映', '98', '猫眼综合评分', '6.9', '某投资银行的经理戴瑞克李（黄立行 饰）正忙于对某锂矿企业进行收购，而他的前任女友艾米梁（徐静蕾 饰）此时也来到香港，以另一家投资银行副总经理的身份担任该锂矿企业的财务顾问，这对昔日情人即将在职场展开对决。戴瑞克的得力助手瑞贝卡（梁咏琪 饰）试图以好友身份劝说艾米退出，而热门金融博主亨利（李治廷 饰）发现了李、梁二人的过往，原来艾米因戴瑞克事业繁忙，绝少时间陪伴自己而赌气分手，不久却在戴瑞克的安排下，亦加入了投行领域。戴瑞克与艾米在飞往伦敦的航班上巧遇，为了从收购案中胜出，二人连番缠斗，而随着事情的发展，这对儿亲密敌人的关系再度变化……', 'http://p0.meituan.net/movie/46534b1a93ec8ab9bcc9ab7dc9e629931755543.jpg', '10', '3085684', 'http://p0.meituan.net/movie/42dfdabfd31136b14b43cca7cc49633457511.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/0eed34125316d3ceb4f7e28397d2234063405.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/a1306aa51d8bb61ebb46ffccffbd5102135722.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/35180879593f7c550347eb47417bd8f438972.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/2a3f43c0d1b6eab1bb81aadcdde804a4103625.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/60da28bcc86f8b5f6038575ff8fc902395494.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/9f4768c869d7c3ec4d0313bf3e7dcf8c81655.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/7b898e91f6a56767129c68ea82ebfae377188.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/a31500a7b989329000d0fdb082bed73970933.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/8eb6fdddb764b4fa7fa6aae5babfb9f2113232.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/204c980bccfd8f2ed359f4af07b0a97a98763.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/d724e60b90ccfc3400f94bfa3b84395c61680.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/c2893bba9c42ed15492ba48697915fcd81489.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/b0c5ea43534bbac9bc0945c0680266c9147563.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/4eafcb7e1b2ff7824d0684ae2f1d749346615.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/97eeb72ea0b803a07f71864e81732d9477849.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/7c20ae6e70487e2257f45c20cec377c073943.jpg@2500w_2500h_1l_0e,http: