INSERT INTO `movie` VALUES (84, '超时空救兵', 'Ultra Reinforcement', '喜剧,动作,科幻', '霍建华,景甜,郭品超', '林子聪', '2012-01-24中国大陆上映', '90', '猫眼综合评分', '5.2', '立志成为作家的青年二蛋（霍建华 饰）历时三年，写成唐朝侠客史可进之传奇故事，然而却被出版社社长嘲讽打击，在一千多年才轮回一次的流星雨之夜，失落的二蛋在公园排遣郁闷，突然胸前的家传玉佩灵光乍现，将他带回了一千多年前的唐朝天宝十年中秋夜，二蛋机缘巧合在唐朝成为了史可进（郭品超 饰）的救命恩人，得以入住范阳王府史家，并对史可进的小妹灵芝（景甜 饰）一见倾心。史家世子史可延嫉妒史可进人财俱得，派出杀手杨傲（孙浩 饰）刺杀亲弟，危急关头二蛋再度使用玉佩，将史家兄妹带回现代，杨傲凭借自己的玉佩同样来到了2012年，二蛋与三位身怀绝技的古代人引发连串奇遇，而一个阴谋也逐渐浮出水面。', 'http://p0.meituan.net/movie/5f547c0cee4834f9e566644595555cdc377552.jpg', '23', '117838', 'http://p0.meituan.net/movie/5a75325d0890ecdcd77414b5ecc2b386202139.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/b9f4ae9ca1d6cc1eee136bf23038d4a0176851.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/ac49401f67750a5e7d103bfe149ebeb2171429.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/a2cabdebb07bd4152dfe0eb6f9f672a3409492.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/43b4b4b35d5fa92082c37f2556a0678d118355.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/7d8b2e3e0bc3875a898869b52bef53e8138158.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/e34a0b02ae57510852420f6e66b25e6571673.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/f334eed4296de2755d99636eace06b04112532.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/b131994a7f62b005577b25da99691dd6103901.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/d0ff4476bb499eedd768d39233f9e35d24210.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/715d18688e03e33738af0d7bd04e52db73638.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/d4a2275c5e69574fed871faca61359e260168.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/d426814c5fdcb39eefa6576607736e3775685.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/5933ce98d593021507d3f3817804cb9a23217.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/a77859ae4ecf24c2a86ec5039f4bf9b792404.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/7f036da3a3c27ee14e00f498c8ac4d7984922.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/9f98039e81405a079ff0d0ecf45da3c073