INSERT INTO `movie` VALUES (90, '自古英雄出少年之岳飞', 'Little Heroes Legend Of Yuefei', '喜剧,古装', '张真,任鹏亮,龚若兰', '朱赵伟', '2012-01-23中国大陆上映', '90', '猫眼综合评分', '3.7', '少年岳飞和几个小伙伴生活在岳家庄。 北宋年间，岳家庄开学堂了，学堂的先生是名震天下的周侗。周侗很喜欢岳飞，收岳飞为徒，教他武功。其他孩子也想练武，周侗的孙女周遥当了他们的“教头”。汤阴县令的儿子潘骏也进了学堂，和岳飞处处为敌。 周侗昔日的徒弟史文恭从梁山死里逃生，潜入汤阴，阴差阳错收潘骏为徒。史文恭绑架了周遥，逼周侗交出所有武功典籍。 周侗受伤后，岳飞带领牛皋等小伙伴巧布机关，把史文恭和几个手下打得屁滚尿流。', 'http://p1.meituan.net/movie/a05528693bea159692040701c2e65cfd144132.jpg', '10', '15146', 'http://p0.meituan.net/movie/35144d16dea36aff1d63fc33c74c8571223025.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/972d3e45b961b87c8c6245e90f7ce799183941.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/f0b79e43c5f5536f49b46f9eb2c1f8f8201476.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/aad4c469a8417676d2d1459d641dc823137593.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/9cf4f5c837e87cb9fcb67e1bcc88439354544.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/eec50ddb0be9468bf52e6367c098f76349531.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/f3d2a24884499cd4546a84fbb1930b4a283696.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/8bb79034bb31ad54c18da828b1d608d3258323.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/cf17c951ca0306f2e05f79ee97dbbc81223660.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/53e5903c70fe4ce9c7741dca57efa34e128195.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/7e75b2997c3e74d838ced5ff87a3fe7d123223.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/d9cace026537adc61928d62080952593148526.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/f1028f54b3ddf761abdc837abdffff5c181265.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/fb52988526ad2dfde997d38a468607bf131136.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/d0f34b688dc1eed16bdc1b2559c2082c179486.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/515ffc7563beac8fe75bb331a8133095206006.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/8225803a8dd57da33ef8e9fdf2a80fc5125007.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/2eaabe8a