INSERT INTO `movie` VALUES (102, '浮出水面的影子', 'The Floating Shadow', '剧情,悬疑,惊悚', '李佳,沈傲君,姜武', '贾东朔', '2012-02-17中国大陆上映', '95', '猫眼综合评分', '5.2', '故事发生在某女子监狱中。深夜子时，万籁俱寂，女囚周露（李佳 饰）面无表情地环视着阴暗的牢房，压抑着狂野的情绪，时间一分一秒过去，她像下定决心般把双手伸向了睡在一旁的另一位狱友。黑夜过去，白昼来临，在遭众狱友轮番凌辱后，周露被管教经纬带进了发泄室中。监房高处窗中射入刺眼的阳光，阳光穿透了周露伤痕累累的胳膊，那是她数次自杀未果的标记。对着墙上斑驳的镜片，她陷入痛苦难奈的回忆之中，冥冥间她似乎看到了早亡的父亲，以及那个沾血的洋娃娃，又似乎看见了继父那双伸向自己的魔爪，还有被逼的疯癫的母亲，随后又出现了向自己施暴的丈夫，周露彻底崩溃了......', 'http://p0.meituan.net/movie/f071b6f49b67781d75c543e59d01ee6d360823.jpg', '25', '69054', 'http://p0.meituan.net/movie/73ccfaf6e9a25b47ed3a20b22aab727a44663.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/fa44146db39ee4c2df0b7b40a1bb45b351839.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/ed089b7499c65352423b5e0e2570850f42385.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/1d13edbfc1cb006c3d41229c5fdffdc350597.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/1669e466471e5b29ea9efa3734fa3d7f53163.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/e34ae4cee9a0b4071a9d4d42f7d7222570774.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/43651f88a6eac1b348b43adbd408e47377283.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/5d49b5b2fdb3ea50b8d6c7b5ade54d1867555.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/17a0a652b465c088cffc6bc7b6371735171110.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/__36915477__9259834.jpg,http://p1.meituan.net/movie/__36915478__9356332.jpg,http://p1.meituan.net/movie/__36915478__2693154.jpg,http://p0.meituan.net/movie/__36915478__1067764.jpg,http://p0.meituan.net/movie/01fdaf269f71de57c8fe9001d140841a68501.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/4f7c11cb4561ba6dd2c930022aef1b6972384.jpg@2500w_2500h_1l_0e,http://p1.meituan.net/movie/ea2169203d85a06476724260b69d3a9e105420.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/ed9540679fd958c23dcce349cbb36eeb348750.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/f071b6f49b67781d75c543e59d01ee6d360823.jpg@2500w_2500h_1l_0e,http://p0.meituan.net/movie/72fab5e6e851be721deb